import React from "react";
import Widget from "components/Widget/index";
import {Table} from "antd";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import { useRedirectToItem } from './utils/navigation';

const redirectToItem = useRedirectToItem();
const handleButtonClick = (id,type) => {
  redirectToItem(id,type);
};

const columns = [
  {
    title: 'Policyholder Name',
    dataIndex: 'image',
    render: (text, record) => {
      return <div className="gx-flex-row gx-align-items-center">
        <img className="gx-rounded-circle gx-size-30 gx-mr-2" src={text} alt=""/>
        <p className="gx-mb-0">{record.name}</p>
      </div>
    },
  },
  {
    title: 'Date Entered',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.transfer}</span>
    },

  },
  {
    title: 'Action',
    dataIndex: 'status',
    render: (text,record) => {
     
      return <span className="gx-text-primary gx-pointer" onClick={()=>handleButtonClick(record.id,'claim')}>
        <i className="icon icon-forward gx-fs-sm gx-mr-2"/>{record.status}</span>
    },
  },

];

const data = [
  {
    key: '1',
    name: 'Jeniffer L.',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View'
  },
  {
    key: '2',
    name: 'Jim Green',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View'
  },
  {
    key: '3',
    name: 'Joe Black',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View'
  }
];



const RecentClaims = () => {

  return (
      <Widget
      title={
        <h2 className="h4 gx-text-capitalize gx-mb-0">
          Recent Claims</h2>
      } extra={
      <p className="gx-text-primary gx-mb-0 gx-pointer gx-d-none gx-d-sm-block">
        <Link to="/lists/claims">
        <i className="icon icon-crm gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> View All Claims
        </Link>
        </p>
    }>
      <div className="gx-table-responsive">
        <Table className="gx-table-no-bordered" columns={columns} dataSource={data} pagination={false}
               size="small"/>
      </div>
      <p className="gx-text-primary gx-mb-0 gx-pointer gx-d-block gx-d-sm-none gx-mb-0 gx-mt-3">
        <i className="icon icon-crm gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> View All Claims</p>
    </Widget>
  );
};

export default RecentClaims;
