import React, { useState } from 'react';
import { Button, Col, DatePicker, Drawer, Form, Row, Select, Avatar,Typography,Input } from 'antd';
import CustomScrollbars from "util/CustomScrollbars";
const { Option } = Select;
const { Title } = Typography;

const AddNote = ({title,icon,txt='primary'}) => {
  const [open, setOpen] = useState(false);
  const showDrawer = () => {
    setOpen(true);
  };
  const onClose = () => {
    setOpen(false);
  };
  const layout = {
    labelCol: { span: 24 },
    wrapperCol: { span: 24 },
  };
  return (

    <>
       <p className={`gx-text-${txt} gx-mb-0 gx-pointer gx-d-none gx-d-sm-block`} onClick={showDrawer}>
       <i className={`icon icon-${icon} gx-fs-lg gx-d-inline-flex gx-vertical-align-middle`} /> {title}
       </p>
     
      <Drawer
        title="Notes"
        size='large'
        width='100%'
        onClose={onClose}
        open={open}
        styles={{
          body: {
            paddingBottom: 80,
          },
        }}
       
      >
     
     <div className="gx-chat-sidenav-main">
      <div className="gx-bg-grey-light gx-chat-sidenav-header">

        <div className="gx-chat-user-hd gx-mb-0">
          <i className="gx-icon-btn icon icon-arrow-left"/>

        </div>
        <div className="gx-chat-user gx-chat-user-center">
          <div className="gx-chat-avatar gx-mx-auto">
            <Avatar src={"https://via.placeholder.com/150"}
                    className="gx-size-60" alt="John Doe"/>
          </div>

          <div className="gx-user-name h4 gx-my-2">Robert Johnson</div>

        </div>
      </div>
      <div className="gx-chat-sidenav-content">

        <CustomScrollbars className="gx-chat-sidenav-scroll">
          <div className="gx-p-4">
          <Form.Item
      label="TextArea"
      name="TextArea"
      rules={[{ required: true, message: 'Please input!' }]}
    >
      <Input.TextArea />
    </Form.Item>
          </div>
        </CustomScrollbars>

      </div>
    </div>
      </Drawer>
    </>
  );
};
export default AddNote;
