import React from "react";
import {Avatar, Checkbox, Dropdown, Menu} from "antd";

import AddUser from "../../AddUser/index";

const options = [
  'Edit',
  'Delete',
];

class UserCell extends React.Component {

  onUserClose = () => {
    this.setState({addUserState: false});
  };
  onDeleteUser = (user) => {
    this.setState({addUserState: false});
    this.props.onDeleteUser(user);
  };
  onEditUser = () => {
    this.setState({addUserState: true});
  };
  menus = () => (<Menu onClick={(e) => {
    if (e.key === 'Edit') {
      this.onEditUser()
    } else {
      this.onDeleteUser(this.props.user)
    }
  }
  }>
    {options.map(option =>
      <Menu.Item key={option}>
        {option}
      </Menu.Item>,
    )}
  </Menu>);

  constructor() {
    super();
    this.state = {
      addUserState: false,
    }
  }

  render() {
    const {user, addFavourite, onUserSelect, onSaveUser} = this.props;
    const {addUserState} = this.state;
    const {name, thumb, email, phone, designation, starred} = user;

    return (

      <div className="gx-contact-item">
        <div className="gx-module-list-icon">
          <Checkbox className="gx-icon-btn"
                    checked={user.selected}
                    value="checkedF"
                    onClick={() => {
                      onUserSelect(user)
                    }}/>
          <div className="gx-d-none gx-d-sm-flex" onClick={() => {
            addFavourite(user)
          }}>
            {starred ? <i className="gx-icon-btn icon icon-star"/> : <i className="gx-icon-btn icon icon-star-o gx-btn-primary"/>}
          </div>
          <div className="gx-ml-2 gx-d-none gx-d-sm-flex">
            {(thumb === null || thumb === '') ?
              <Avatar size="large">
                {name.charAt(0).toUpperCase()}
              </Avatar>
              :
              <Avatar size="large" alt={name} src={thumb}/>
            }
          </div>
        </div>

        <div className="gx-module-list-info gx-contact-list-info">
          <div className="gx-module-contact-content">
            <p className="gx-mb-1">
              <span className="gx-text-truncate gx-contact-name"> {name} </span>
              <span className="gx-toolbar-separator">&nbsp;</span>
              <span className="gx-text-truncate gx-job-title">{designation}</span>
            </p>

            <div className="gx-text-muted">
            <span className="gx-email gx-d-inline-block gx-mr-2">
                {email},
            </span>
              <span className="gx-phone gx-d-inline-block">{phone}</span>
            </div>
          </div>

          <div className="gx-module-contact-right">

            <Dropdown overlay={this.menus()} placement="bottomRight" trigger={['click']}>
              <i className="gx-icon-btn icon icon-ellipse-v"/>
            </Dropdown>

            {addUserState &&
            <AddUser open={addUserState} user={user} onSaveUser={onSaveUser}
                        onUserClose={this.onUserClose} onDeleteUser={this.onDeleteUser}/>}
          </div>
        </div>
      </div>
    )
  }
}

export default UserCell;
