import React from "react";
import {Avatar, Checkbox} from "antd";

import labels from "routes/lists/Doctors/data/labels";
const DoctorListItem = ({doctor, onDoctorSelect, onDoctorChecked, onStartSelect}) => {
  return (
    <div className="gx-module-list-item gx-mail-cell">
      <div className="gx-module-list-icon">
        <Checkbox color="primary" className="gx-icon-btn"
                  checked={doctor.selected}
                  onClick={(event) => {
                    event.stopPropagation();
                    onDoctorChecked(doctor)
                  }}
                  value="SelectDoctor"
        />
        <div onClick={() => {
          onStartSelect(doctor);
        }}>
          {doctor.starred ?
            <i className="gx-icon-btn icon icon-star"/> :
            <i className="gx-icon-btn icon icon-star-o"/>
          }

        </div>

        <div className="gx-ml-2">
          {doctor.from.avatar === '' ?
            <Avatar className="gx-avatar gx-bg-blue gx-size-40"> {doctor.from.name.charAt(0).toUpperCase()}</Avatar> :
            <Avatar className="gx-size-40" alt="Alice Freeman"
                    src={doctor.from.avatar}/>
          }
        </div>
      </div>

      <div className="gx-mail-list-info" onClick={() => {
        onDoctorSelect(doctor);
      }}>

        <div className="gx-module-list-content">
          <div className="gx-mail-user-des">

            <span className="gx-sender-name">{doctor.from.name}</span>

            <span className="gx-toolbar-separator">&nbsp;</span>

            <span className="gx-d-inline-block gx-text-truncate gx-send-subject">{doctor.practice_number}</span>

            {doctor.hasAttachments &&

            <i className="icon icon-attachment"/>}

            <div className="gx-time">{doctor.date_entered}</div>

          </div>


          <div className="gx-message">
            <p className="gx-text-truncate"> {doctor.discipline}</p>

          </div>
          <div className="gx-labels">
            {labels.map((label, index) => {
              return (doctor.labels).includes(label.id) &&
                <div key={index} className={`gx-badge gx-text-white gx-bg-${label.color}`}>{label.title}</div>
            })}
          </div>
        </div>

      </div>

    </div>
  )
};

export default DoctorListItem;
