import React, {PureComponent} from "react";
import {Button, Checkbox, Drawer, Dropdown, Menu, message} from "antd";
import CustomScrollbars from "util/CustomScrollbars";

import doctors from "./data/doctors";
import folders from "./data/folders";
import filters from "./data/filters";
import labels from "./data/labels";
import options from "./data/options";
import DoctorList from "components/lists/DoctorsList/DoctorList";
import AddDoctor from "components/lists/DoctorsList/AddDoctor/index";
import AppModuleHeader from "components/AppModuleHeader/index";
import DoctorDetail from "components/lists/DoctorsList/DoctorDetail/index";
import IntlMessages from "util/IntlMessages";
import CircularProgress from "../../../components/CircularProgress/index";
import Auxiliary from "../../../util/Auxiliary";


class Doctors extends PureComponent {

  DoctorSideBar = () => {
    return <div className="gx-module-side">

      <div className="gx-module-side-header">
        <div className="gx-module-logo">
          <i className="icon icon-chat gx-mr-4"/>
          <IntlMessages id="sidebar.doctor.doctors"/>
        </div>
      </div>

      <div className="gx-module-side-content">
        <CustomScrollbars className="gx-module-side-scroll">
          <div className="gx-module-add-task">
            <Button type="primary" className="gx-btn-block"
                    onClick={() => {
                      this.setState({addDoctor: true})
                    }}>
              <i className="icon icon-edit gx-mr-2"/>
              <IntlMessages id="sidebar.doctor.doctors"/></Button>
          </div>

          <ul className="gx-module-nav">
            {this.getNavFolders()}

            <li className="gx-module-nav-label">
              <IntlMessages id="sidebar.doctor.doctors"/>
            </li>

            {this.getNavFilters()}

            <li className="gx-module-nav-label">
              <IntlMessages id="sidebar.doctor.doctors"/>
            </li>

            {this.getNavLabels()}

          </ul>
        </CustomScrollbars>
      </div>
    </div>
  };

  onDeleteMail = () => {
    const doctors = this.state.allMail.map(mail =>
      mail.selected && (mail.folder === this.state.selectedFolder) ?
        {...mail, folder: 4, selected: false,} : mail
    );
    this.setState({
      alertMessage: 'Mail Deleted Successfully',
      showMessage: true,
      selectedDoctors: 0,
      allMail: doctors,
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
  };

  getNavFolders = () => {
    return folders.map((folder, index) =>
      <li key={index} onClick={() => {
        const filterDoctors = this.state.allMail.filter(mail => mail.folder === folder.id);
        this.setState({
          selectedFolder: folder.id,
          noContentFoundMessage: 'No Mail found in selected folder',
          currentDoctor: null,
          loader: true,
          folderDoctors: filterDoctors
        });
        setTimeout(() => {
          this.setState({loader: false});
        }, 1500);
      }
      }>
        <span className={`${this.state.selectedFolder === folder.id ? 'active gx-link' : 'gx-link'}`}>
          <i className={`icon icon-${folder.icon}`}/>
          <span>{folder.title}</span>
        </span>
      </li>
    )
  };


  onFolderMenuItemSelect = (e) => {
    const id = +e.key;
    const doctors = this.state.allMail.map(mail =>
      mail.selected && (mail.folder === this.state.selectedFolder) ?
        {...mail, folder: id, selected: false,} : mail
    );
    this.setState({
      selectedDoctors: 0,
      allMail: doctors,
      noContentFoundMessage: 'No Mail found in selected folder',
      alertMessage: 'Mail has been moved successfully',
      showMessage: true,
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
  };

  onLabelMenuItemSelect = (e) => {
    const id = +e.key;
    const doctors = this.state.allMail.map(mail => {
        if (mail.selected && (mail.folder === this.state.selectedFolder)) {
          if (mail.labels.includes(id)) {
            return {...mail, labels: this.removeLabel(mail, id)};
          } else {
            return {...mail, labels: this.addLabel(mail, id)};
          }
        } else {
          return mail;
        }
      }
    );
    this.setState({
      noContentFoundMessage: 'No Mail found in selected label',
      alertMessage: 'Label Updated Successfully',
      showMessage: true,
      allMail: doctors,
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
  };

  handleRequestClose = () => {
    this.setState({
      addDoctor: false,
      showMessage: false,
    });
  };
  getNavFilters = () => {
    return filters.map((filter, index) =>
      <li key={index} onClick={() => {
        const filterDoctors = this.state.allMail.filter(mail => {
          if (filter.id === 0 && mail.starred && mail.folder === this.state.selectedFolder) {
            return mail
          } else if (filter.id === 1 && mail.important && mail.folder === this.state.selectedFolder) {
            return mail
          }
          return null;
        });
        this.setState({
          noContentFoundMessage: 'No Mail found in selected filter',
          loader: true,
          folderDoctors: filterDoctors
        });
        setTimeout(() => {
          this.setState({loader: false});
        }, 1500);
      }
      }>
        <span className="gx-link">
          <i className={`icon icon-${filter.icon}`}/>
          <span>{filter.title}</span>
        </span>
      </li>
    )
  };
  onOptionMenuItemSelect = (e) => {
    switch (e.key) {
      case 'All':
        this.handleRequestClose();
        this.getAllMail();
        break;
      case 'None':
        this.handleRequestClose();
        this.getUnselectedAllMail();
        break;
      case 'Read':
        this.handleRequestClose();
        this.getReadMail();
        break;
      case 'Unread':
        this.handleRequestClose();
        this.getUnreadMail();
        break;
      case 'Starred':
        this.handleRequestClose();
        this.getStarredMail();
        break;
      case 'Unstarred':
        this.handleRequestClose();
        this.getUnStarredMail();
        break;
      case 'Important':
        this.handleRequestClose();
        this.getImportantMail();
        break;
      case 'Unimportant':
        this.handleRequestClose();
        this.getUnimportantMail();
        break;
      default:
        break;
    }
  };

  getAllMail = () => {
    let doctors = this.state.allMail.map((doctor) => mail.folder === this.state.selectedFolder ? {
      ...mail,
      selected: true
    } : mail);
    this.setState({
      selectedDoctors: doctors.length,
      allMail: doctors,
      optionName: 'All',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
  };
  getUnselectedAllMail = () => {
    let doctors = this.state.allMail.map((doctor) => mail.folder === this.state.selectedFolder ? {
      ...mail,
      selected: false
    } : mail);
    this.setState({
      selectedDoctors: 0,
      allMail: doctors,
      optionName: 'None',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
  };
  getReadMail = () => {
    let selectedMail = 0;
    let doctors = this.state.allMail.filter(mail => mail.folder === this.state.selectedFolder);
    doctors = doctors.map((doctor) => {
      if (mail.read) {
        selectedMail++;
        return {...mail, selected: true};
      }
      return {...mail, selected: false}
    });
    this.setState({
      selectedDoctors: selectedMail,
      optionName: 'Read',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
    return doctors;
  };
  getUnreadMail = () => {
    let selectedMail = 0;
    let doctors = this.state.allMail.filter(mail => mail.folder === this.state.selectedFolder);
    doctors = doctors.map((doctor) => {
      if (!mail.read) {
        selectedMail++;
        return {...mail, selected: true};
      }
      return {...mail, selected: false}
    });
    this.setState({
      selectedDoctors: selectedMail,
      optionName: 'Unread',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
    return doctors;
  };
  getStarredMail = () => {
    let selectedMail = 0;
    let doctors = this.state.allMail.filter(mail => mail.folder === this.state.selectedFolder);
    doctors = doctors.map((doctor) => {
      if (mail.starred) {
        selectedMail++;
        return {...mail, selected: true};
      }
      return {...mail, selected: false}
    });
    this.setState({
      selectedDoctors: selectedMail,
      optionName: 'Starred',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
    return doctors;
  };
  getUnStarredMail = () => {
    let selectedMail = 0;
    let doctors = this.state.allMail.filter(mail => mail.folder === this.state.selectedFolder && mail.folder === this.state.selectedFolder);
    doctors = doctors.map((doctor) => {
      if (!mail.starred) {
        selectedMail++;
        return {...mail, selected: true};
      }
      return {...mail, selected: false}
    });
    this.setState({
      selectedDoctors: selectedMail,
      optionName: 'UnStarred',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
    return doctors;
  };
  getImportantMail = () => {
    let selectedMail = 0;
    let doctors = this.state.allMail.filter(mail => mail.folder === this.state.selectedFolder && mail.folder === this.state.selectedFolder);
    doctors = doctors.map((doctor) => {
      if (mail.important) {
        selectedMail++;
        return {...mail, selected: true};
      }
      return {...mail, selected: false}
    });
    this.setState({
      selectedDoctors: selectedMail,
      optionName: 'Important',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
    return doctors;
  };
  getUnimportantMail = () => {
    let selectedMail = 0;
    let doctors = this.state.allMail.filter(mail => mail.folder === this.state.selectedFolder && mail.folder === this.state.selectedFolder);
    doctors = doctors.map((doctor) => {
      if (!mail.important) {
        selectedMail++;
        return {...mail, selected: true};
      }
      return {...mail, selected: false}
    });
    this.setState({
      selectedDoctors: selectedMail,
      optionName: 'Unimportant',
      noContentFoundMessage: 'No Mail found in selected Label',
      folderDoctors: doctors.filter(mail => mail.folder === this.state.selectedFolder)
    });
    return doctors;
  };
  getNavLabels = () => {
    return labels.map((label, index) =>
      <li key={index} onClick={() => {
        const filterDoctors = this.state.allMail.filter(mail => mail.labels.includes(label.id) && mail.folder === this.state.selectedFolder);
        this.setState({
          loader: true,
          noContentFoundMessage: 'No Mail found in selected label',
          folderDoctors: filterDoctors
        });
        setTimeout(() => {
          this.setState({loader: false});
        }, 1500);
      }
      }>
        <span className="gx-link">
          <i className={`icon icon-tag gx-text-${label.color}`}/>
          <span>{label.title}</span>
        </span>
      </li>
    )
  };
  searchDoctor = (searchText) => {
    if (searchText === '') {
      this.setState({folderDoctors: this.state.allDoctor.filter((doctor) => !doctor.deleted)});
    } else {
      const searchDoctors = this.state.allDoctor.filter((doctor) =>
        !doctor.deleted && doctor.subject.toLowerCase().indexOf(searchText.toLowerCase()) > -1);
      this.setState({
        folderDoctors: searchDoctors
      });
    }
  };
  displayDoctor = (currentDoctor, folderDoctors, noContentFoundMessage) => {
    return (<div className="gx-module-box-column">
      {currentDoctor === null ?
        folderDoctors.length === 0 ?
          <div className="gx-no-content-found gx-text-light gx-d-flex gx-align-items-center gx-justify-content-center">
            {noContentFoundMessage}
          </div>
          :
          <DoctorList doctors={folderDoctors} onStartSelect={this.onStartSelect.bind(this)}
                    onDoctorSelect={this.onDoctorSelect.bind(this)}
                    onDoctorChecked={this.onDoctorChecked.bind(this)}/>
        :
        <DoctorDetail doctor={currentDoctor} onStartSelect={this.onStartSelect.bind(this)}
                    onImportantSelect={this.onImportantSelect.bind(this)}/>}
    </div>)
  };
  getDoctorActions = () => {
    return <div className="gx-flex-row gx-align-items-center">

      <Dropdown overlay={this.folderMenu()} placement="bottomRight" trigger={['click']}>
        <i className="icon icon-folder gx-icon-btn"/>
      </Dropdown>

      <Dropdown overlay={this.labelMenu()} placement="bottomRight" trigger={['click']}>
        <i className="icon icon-tag gx-icon-btn"/>
      </Dropdown>

      <span onClick={this.onDeleteDoctor.bind(this)}>
        <i className="icon icon-trash gx-icon-btn"/></span>

    </div>
  };

  optionMenu = () => (
    <Menu id="option-menu"
          onClick={this.onOptionMenuItemSelect.bind(this)}>
      {options.map(option =>
        <Menu.Item key={option.title}>
          {option.title}
        </Menu.Item>,
      )}
    </Menu>);

  folderMenu = () => (
    <Menu id="folder-menu"
          onClick={this.onFolderMenuItemSelect.bind(this)}>
      {folders.map(folder =>
        <Menu.Item key={folder.id}>
          {folder.title}
        </Menu.Item>,
      )}
    </Menu>);

  labelMenu = () => (
    <Menu id="label-menu"
          onClick={this.onLabelMenuItemSelect.bind(this)}>
      {labels.map(label =>
        <Menu.Item key={label.id}>
          {label.title}
        </Menu.Item>,
      )}
    </Menu>
  );


  constructor() {
    super();
    this.state = {
      searchDoctor: '',
      noContentFoundMessage: 'No Doctor found in selected folder',
      alertMessage: '',
      showMessage: false,
      drawerState: false,
      optionName: 'None',
      anchorEl: null,
      allDoctor: doctors,
      loader: true,
      currentDoctor: null,
      user: {
        name: 'Robert Johnson',
        email: 'robert@example.com',
        avatar: "https://via.placeholder.com/150"
      },
      selectedDoctors: 0,
      selectedFolder: 0,
      addDoctor: false,
      folderDoctors: doctors.filter(doctor => doctor.folder === 0)
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({loader: false});
    }, 1500);
  }


  onDoctorChecked(data) {
    data.selected = !data.selected;
    let selectedDoctor = 0;
    const doctors = this.state.folderDoctors.map(doctor => {
        if (doctor.selected) {
          selectedDoctor++;
        }
        if (doctor.id === data.id) {
          if (doctor.selected) {
            selectedDoctor++;
          }
          return data;
        } else {
          return doctor;
        }
      }
    );
    this.setState({
      selectedDoctors: selectedDoctor,
      folderDoctors: doctors
    });
  }

  onAllDoctorSelect() {
    const selectAll = this.state.selectedDoctors <= this.state.folderDoctors.length;
    if (selectAll) {
      this.getAllDoctor();
    } else {
      this.getUnselectedAllDoctor();
    }
  }

  removeLabel(doctor, label) {
    doctor.labels.splice(doctor.labels.indexOf(label), 1);
    if (this.state.currentDoctor !== null && doctor.id === this.state.currentDoctor.id) {
      this.setState({
        currentDoctor: {...doctor, labels: doctor.labels}
      })
    }
    return doctor.labels;
  }

  onStartSelect(data) {
    data.starred = !data.starred;
    this.setState({
      alertMessage: data.starred ? 'Doctor Mark as Active' : 'Doctor Remove as Active',
      showMessage: true,
      folderDoctors: this.state.folderDoctors.map(doctor =>
        doctor.id === data.id ?
          data : doctor
      )
    });
  }

  onImportantSelect(data) {
    data.important = !data.important;
    this.setState({
      alertMessage: data.important ? 'Doctor Mark as Important' : 'Doctor Remove as Important',
      showMessage: true,
      folderDoctors: this.state.folderDoctors.map(doctor =>
        doctor.id === data.id ?
          data : doctor
      )
    });
  }

  onDoctorSend(data) {
    this.setState(
      {
        alertMessage: 'Doctor Sent Successfully',
        showMessage: true,
        folderDoctors: this.state.allDoctor.concat(data),
        allDoctor: this.state.allDoctor.concat(data)
      }
    );
  }

  onDoctorSelect(doctor) {
    this.setState({
      loader: true,
      currentDoctor: doctor,
    });
    setTimeout(() => {
      this.setState({loader: false});
    }, 1500);
  }

  addLabel(doctor, label) {
    if (this.state.currentDoctor !== null && doctor.id === this.state.currentDoctor.id) {
      this.setState({
        currentDoctor: {...doctor, labels: doctor.labels.concat(label)}
      })
    }
    return doctor.labels.concat(label)
  }

  updateSearch(evt) {
    this.setState({
      searchDoctor: evt.target.value,
    });
    this.searchDoctor(evt.target.value)
  }

  onToggleDrawer() {
    this.setState({
      drawerState: !this.state.drawerState
    });
  }

  render() {
    const {selectedDoctors, loader, currentDoctor, drawerState, folderDoctors, addDoctor, user, alertMessage, showMessage, noContentFoundMessage} = this.state;
    return (

      <div className="gx-main-content">
        <div className="gx-app-module">

          <div className="gx-d-block gx-d-lg-none">
            <Drawer
              placement="left"
              closable={false}
              visible={drawerState}
              onClose={this.onToggleDrawer.bind(this)}>
              {this.DoctorSideBar()}
            </Drawer>

          </div>
          <div className="gx-module-sidenav gx-d-none gx-d-lg-flex">
            {this.DoctorSideBar()}
          </div>

          <div className="gx-module-box">
            <div className="gx-module-box-header">
              <span className="gx-drawer-btn gx-d-flex gx-d-lg-none">
                  <i className="icon icon-menu gx-icon-btn" aria-label="Menu"
                     onClick={this.onToggleDrawer.bind(this)}/>
              </span>
              <AppModuleHeader placeholder="Search Doctors" user={this.state.user}
                               onChange={this.updateSearch.bind(this)}
                               value={this.state.searchDoctor}/>

            </div>

            <div className="gx-module-box-content">
              <div className="gx-module-box-topbar">
                {this.state.currentDoctor === null ?
                  <div className="gx-flex-row gx-align-items-center">
                    {this.state.folderDoctors.length > 0 ?
                      <Auxiliary>
                        <Checkbox color="primary" className="gx-icon-btn"
                                  indeterminate={selectedDoctors > 0 && selectedDoctors < folderDoctors.length}
                                  checked={selectedDoctors > 0}
                                  onChange={this.onAllDoctorSelect.bind(this)}
                                  value="SelectDoctor"/>

                        <Dropdown overlay={this.optionMenu()} placement="bottomRight" trigger={['click']}>
                          <div>
                            <span className="gx-px-2"> {this.state.optionName}</span>
                            <i className="icon icon-charvlet-down"/></div>
                        </Dropdown>
                      </Auxiliary>
                      : null}
                  </div>
                  :
                  <i className="icon icon-arrow-left gx-icon-btn" onClick={() => {
                    this.setState({currentDoctor: null})
                  }}/>
                }

                <div classID="toolbar-separator"/>

                {(selectedDoctors > 0) && this.getDoctorActions()}

              </div>

              {loader ?
                <div className="gx-loader-view">
                  <CircularProgress/>
                </div>
                : this.displayDoctor(currentDoctor, folderDoctors, noContentFoundMessage)}

              <AddDoctor open={addDoctor} user={user}
                           onClose={this.handleRequestClose.bind(this)}
                           onDoctorSend={this.onDoctorSend.bind(this)}/>

            </div>
          </div>
        </div>
        {showMessage && message.info(<span id="message-id">{alertMessage}</span>, 3, this.handleRequestClose)}
      </div>
    )
  }
}

export default Doctors;
