import React,{useState} from "react";
import Widget from "components/Widget/index";
import {Button, Col, Row} from "antd";
import LineIndicator from "./LineIndicator";
import AddProduct from "components/lists/ProductList/AddProduct";

const ClaimsPayable = () => {
   const [isModalOpen, setIsModalOpen] = useState(false);
  const toggleModal = () => {
    setIsModalOpen(true);
  };
  const handleOk = () => {
    setIsModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };
  return (
    <Widget>
      <h2 className="h4 gx-mb-3">Total Claims Payable</h2>
      <Row>
        <Col lg={12} md={12} sm={12} xs={24}>

          <div className="ant-row-flex">
            <h2 className="gx-mr-2 gx-mb-0 gx-fs-xxxl gx-font-weight-medium">R179,626</h2>
            <h4 className="gx-pt-2 gx-chart-up">64% <i className="icon icon-menu-up gx-fs-sm"/></h4>
          </div>
          <p className="gx-text-grey">Overall balance</p>
          <div className="ant-row-flex gx-mb-3 gx-mb-md-2">
            <Button className="gx-mr-2" type="primary" title="Make Payment">Pay</Button>
            <Button className="gx-btn-cyan" title="Request Payment">Request</Button>
          </div>

          <p className="gx-text-primary gx-pointer gx-d-none gx-d-sm-block gx-mb-1" onClick={() => toggleModal()}>
            <i className="icon icon-add-circle gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> Add New Product</p>
        </Col>
        <Col lg={12} md={12} sm={12} xs={24}>
          <div className="gx-site-dash">
            <h5 className="gx-mb-3">Products Distribution</h5>
            <ul className="gx-line-indicator gx-fs-sm gx-pb-1 gx-pb-sm-0">
              <li>
                <LineIndicator width="78%" title="Product A" title2="R128.74" color="primary" value="78%"/>
              </li>
              <li>
                <LineIndicator width="38%" title="Product B" title2="R128.23" color="pink" value="48%"/>
              </li>
              <li>
                <LineIndicator width="24%" title="Product C" title2="R128.71" color="orange" value="34%"/>
              </li>
            </ul>
           
          </div>
        </Col>
      </Row>
      <AddProduct isOpen={isModalOpen} handleCancel={handleCancel} handleOk={handleOk}/>
    </Widget>
  );
};

export default ClaimsPayable;
