import React from "react";
import Widget from "components/Widget";
import {Table,Badge} from "antd";

const columns = [
  {
    title: 'Tariff Code',
    dataIndex: 'image',
    render: (text, record) => {
      return <div className="gx-flex-row gx-align-items-center">
        <p className="gx-mb-0">{record.name}</p>
      </div>
    },
  },
  {
    title: 'Treatment Date',
    dataIndex: 'claim_status',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.claim_status}</span>
    }
  }`id`, `claim_provider_id`, `amount`, `tariff_code`, `treatment_date`, `icd10_code`, `entered_by`, `date_entered`
  ,
  {
    title: 'ICD10 Code',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.transfer}</span>
    },

  },
  {
    title: 'Amount',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.transfer}</span>
    },   

  },
  ,
  {
    title: 'Status',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.transfer}</span>
    },   

  },
  {
    title: 'Action',
    dataIndex: 'status',
    render: (text) => {
      return <span className="gx-text-primary gx-pointer">
        <i className="icon icon-forward gx-fs-sm gx-mr-2"/>{text}</span>
    },
  },

];

const data = [
  {
    key: '1',
    name: 'R3 454.90',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View',
    claim_status:'Open',
  },
  {
    key: '2',
    name: 'R9 454.90',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View',
    claim_status:'Paid',
  },
  {
    key: '3',
    name: 'R13 454.90',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View',
    claim_status:'Closed',
  }
];



const ClaimLines = () => {
  return (
    <Widget styleName="gx-card-profile" 
    title={
      <h2 className="h4 gx-text-capitalize gx-mb-0">
        Doctor(s) <Badge count={1} showZero color="green" /></h2>
    } extra={
    <p className="gx-text-primary gx-mb-0 gx-pointer gx-d-none gx-d-sm-block">
      <i className="icon icon-add-circle gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> Add New Claim</p>
  }
    >
     <h4 className="gx-text-grey gx-fs-md gx-mb-10">Dr Test Surname | 7586896</h4>    
      <div className="gx-table-responsive">
        <Table className="gx-table-no-bordered" columns={columns} dataSource={data} pagination={false}
               size="small"/>
      </div>      
    </Widget>

  )
}


export default ClaimLines;
