import React from "react";
import {Avatar, Input, Modal,Select} from "antd";

import IntlMessages from "util/IntlMessages";
const { Option } = Select;

class AddUser extends React.Component {
  constructor(props) {
    super(props);

    const {id, thumb, name, email, phone, designation, selected, starred, frequently} = props.user;
    this.state = {
      id,
      thumb,
      name,
      email,
      phone,
      designation,
      selected,
      starred,
      frequently
    }
  }

  render() {
    const {onSaveUser, onUserClose, open, user} = this.props;
    const {id, name, email, phone, designation, selected, starred, frequently} = this.state;
    let {thumb} = this.state;
    if (!thumb) {
      thumb = '/assets/images/placeholder.jpg';
    }
    return (
      <Modal
        title={user.name === '' ?
          <IntlMessages id="contact.addContact"/> :
          <IntlMessages id="contact.saveContact"/>}
        toggle={onUserClose} visible={open}
        closable={false}
        onOk={() => {
          if (name === '')
            return;
          onUserClose();
          onSaveUser(
            {
              'id': id,
              'name': name,
              'thumb': thumb,
              'email': email,
              'phone': phone,
              'designation': designation,
              'selected': selected,
              'starred': starred,
              'frequently': frequently
            });
          this.setState({
            'id': id + 1,
            'name': '',
            'thumb': '',
            'email': '',
            'phone': '',
            'designation': '',
          })

        }}
        onCancel={onUserClose}>

        <div className="gx-modal-box-row">
          <div className="gx-modal-box-avatar">
            <Avatar size="large" src={thumb}/>
          </div>

          <div className="gx-modal-box-form-item">
            <div className="gx-form-group">
              <Input
                required
                placeholder="First Name"
                onChange={(event) => this.setState({name: event.target.value})}
                defaultValue={name}
                margin="none"/>
            </div>
            <div className="gx-form-group">
              <Input
                required
                placeholder="Last Name"
                onChange={(event) => this.setState({name: event.target.value})}
                defaultValue={name}
                margin="none"/>
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="Email"
                onChange={(event) => this.setState({email: event.target.value})}
                value={email}
                margin="normal"/>
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="Contact Number"
                onChange={(event) => this.setState({phone: event.target.value})}
                value={phone}
                margin="normal"
              />
            </div>
            <div className="gx-form-group">
            <Select placeholder="Please Select Role" style={{width:'100%'}}>
                  <Option value="xiao">Admin</Option>
                  <Option value="mao">Ordinary</Option>
                </Select>
            </div>
          </div>
        </div>
      </Modal>
    );
  }
}

export default AddUser;
