import React from "react";
import Widget from "components/Widget";
import {Button, Col, Row,Table} from "antd";
import LineIndicator from "./LineIndicator";

const columns = [
  {
    title: 'Policyholder Name',
    dataIndex: 'image',
    render: (text, record) => {
      return <div className="gx-flex-row gx-align-items-center">
        <img className="gx-rounded-circle gx-size-30 gx-mr-2" src={text} alt=""/>
        <p className="gx-mb-0">{record.name}</p>
      </div>
    },
  },
  {
    title: 'Date Entered',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.transfer}</span>
    },

  },
  {
    title: 'Action',
    dataIndex: 'status',
    render: (text) => {
      return <span className="gx-text-primary gx-pointer">
        <i className="icon icon-forward gx-fs-sm gx-mr-2"/>{text}</span>
    },
  },

];

const data = [
  {
    key: '1',
    name: 'Jeniffer L.',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View'
  },
  {
    key: '2',
    name: 'Jim Green',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View'
  },
  {
    key: '3',
    name: 'Joe Black',
    transfer: '2024-09-09 10:10:10',
    image: "/assets/images/claim.png",
    status: 'View'
  }
];



const PolicyholderClaims = () => {
  return (
    <Widget styleName="gx-card-profile">
      <div className="ant-card-head">
        <span className="ant-card-head-title gx-mb-2">Associated Claims</span>
        <p className="gx-text-grey gx-fs-sm gx-mb-0">These are claims associated to this policyholder</p>
      </div>
      
      <div className="gx-table-responsive">
        <Table className="gx-table-no-bordered" columns={columns} dataSource={data} pagination={false}
               size="small"/>
      </div>
      <p className="gx-text-primary gx-mb-0 gx-pointer gx-d-block gx-d-sm-none gx-mb-0 gx-mt-3">
        <i className="icon icon-add-circle gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> Add New Claim</p>
    </Widget>

  )
}


export default PolicyholderClaims;
