import React, {Component} from "react";
import {Button, Drawer, message} from "antd";
import CustomScrollbars from "util/CustomScrollbars";
import contactList from "./data/contactList";
import AppModuleHeader from "components/AppModuleHeader/index";
import AddContact from "components/contact/AddContact";
import IntlMessages from "util/IntlMessages";
import PolicyholdersList from "../../../components/lists/PolicyholdersList/index";



let contactId = 723812738;

const filterOptions = [
  {
    id: 1,
    name: 'All Policyholders',
    icon: 'all-contacts'
  }, {
    id: 2,
    name: 'Active Policyholders',
    icon: 'star'

  }, {

    id: 3,
    name: 'Inactive Policyholders',
    icon: 'frequent'
  }
];

class Policyholders extends Component {

  ContactSideBar = (user) => {
    return <div className="gx-module-side">
      <div className="gx-module-side-header">
        <div className="gx-module-logo">
          <i className="icon icon-contacts gx-mr-4"/>
          <span><IntlMessages id="chat.policyholders"/></span>
        </div>
      </div>

      <div className="gx-module-side-content">
        <CustomScrollbars className="gx-module-side-scroll">
          <div className="gx-module-add-task">
            <Button className="gx-btn-block ant-btn" type="primary" aria-label="add"
                    onClick={this.onAddContact}>
              <i className="icon icon-add gx-mr-2"/>
              <span>Add New Contact</span>
            </Button>
          </div>
          <div className="gx-module-side-nav">
            <ul className="gx-module-nav">
              {filterOptions.map(option => <li key={option.id} className="gx-nav-item">
                  <span
                    className={`gx-link ${option.id === this.state.selectedSectionId ? 'active' : ''}`} onClick={
                    this.onFilterOptionSelect.bind(this, option)
                  }>
                    <i className={`icon icon-${option.icon}`}/>
                    <span>{option.name}</span>
                  </span>
                </li>
              )}

            </ul>
          </div>
        </CustomScrollbars>
      </div>
    </div>

  };

  addFavourite = (data) => {
    this.setState({
      alertMessage: data.starred ? 'Contact removed as star' : 'Contact marked as star',
      showMessage: true,
      contactList: this.state.contactList.map((contact) => contact.id === data.id ? {
        ...contact,
        starred: !data.starred
      } : contact),
      allContact: this.state.allContact.map((contact) => contact.id === data.id ? {
        ...contact,
        starred: !data.starred
      } : contact)
    })
  };
  onContactSelect = (data) => {
    data.selected = !data.selected;
    let selectedContacts = 0;
    const contactList = this.state.contactList.map(contact => {
        if (contact.selected) {
          selectedContacts++;
        }
        if (contact.id === data.id) {
          if (contact.selected) {
            selectedContacts++;
          }
          return data;
        } else {
          return contact;
        }
      }
    );
    this.setState({
      selectedContacts: selectedContacts,
      contactList: contactList
    });

  };


  onAddContact = () => {
    this.setState({addContactState: true});
  };
  onContactClose = () => {
    this.setState({addContactState: false});
  };
  onFilterOptionSelect = (option) => {
    switch (option.name) {
      case 'All contacts': {
        this.setState({
          selectedSectionId: option.id,
          filterOption: option.name,
          contactList: this.state.allContact
        });
        break;
      }
      case 'Frequently contacted': {
        this.setState({
          selectedSectionId: option.id,
          filterOption: option.name,
          contactList: this.state.allContact.filter((contact) => contact.frequently)
        });
        break;
      }
      case 'Starred contacts': {
        this.setState({
          selectedSectionId: option.id,
          filterOption: option.name,
          contactList: this.state.allContact.filter((contact) => contact.starred)
        });
        break;
      }
      default:
        break;
    }

  };


   constructor() {
    super();
  
  }




  render() {
    const {user, drawerState} = this.state;
    return (
      <div className="gx-main-content">
        <div className="gx-app-module">

          <div className="gx-d-block gx-d-lg-none">
            <Drawer
              placement="left"
              closable={false}
              visible={drawerState}>
              {this.ContactSideBar()}
            </Drawer>
          </div>
          <div className="gx-module-sidenav gx-d-none gx-d-lg-flex">
            {this.ContactSideBar(user)}
          </div>

          <div className="gx-module-box">
            <div className="gx-module-box-header">
              <span className="gx-drawer-btn gx-d-flex gx-d-lg-none">
                  <i className="icon icon-menu gx-icon-btn" aria-label="Menu"                     />
              </span>

              <AppModuleHeader placeholder="Search Policyholder" notification={false} apps={false}
                               user={this.state.user}
                               value={this.state.searchUser}/>
            </div>
            <div className="gx-module-box-content">

              <CustomScrollbars className="gx-module-content-scroll">
                <PolicyholdersList/>
              </CustomScrollbars>

            </div>
          </div>
        </div>

      </div>
    )
  }
}

export default Policyholders;
