import React from "react";
import {Table,Dropdown,Menu,Badge} from "antd";
import Widget from "components/Widget/index";

const options = [
  'Edit',
  'Delete',
];

const menus = () => (<Menu onClick={(e) => {
  if (e.key === 'Edit') {    
  } else {
   
  }
}
}>
  {options.map(option =>
    <Menu.Item key={option}>
      {option}
    </Menu.Item>,
  )}
</Menu>);

const columns = [
  {
    title: 'Product Name',
    dataIndex: 'image',
    sorter: (a, b) => a.product_name - b.product_name,
    render: (text, record) => {
      return <div className="gx-flex-row gx-align-items-center"> 
      <img className="gx-rounded-circle gx-size-30 gx-mr-2" src={text} alt=""/>      
        <p className="gx-mb-0">{record.product_name}</p>
      </div>
    },
  },
  {
    title: 'Start Date',
    dataIndex: 'transfer',
    sorter: (a, b) => a.start_date - b.start_date,
    render: (text, record) => {
      return <span className="gx-text-grey">{record.start_date}</span>
    },

  },

  {
    title: 'End Date',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.end_date}</span>
    },

  },
    {
    title: 'Status',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey"><Badge count={record.product_status} showZero color="green" /></span>
    },

  },
  {
    title: 'Action',
    dataIndex: 'status',
    render: (text) => {
      return <span className="gx-text-primary gx-pointer gx-d-inline-flex">
        <i className="icon icon-forward gx-fs-sm gx-mr-2" title="View"/>
        <Dropdown overlay={menus} placement="bottomRight" title="More" trigger={['click']}>
              <i className="gx-ml-2 icon icon-ellipse-v"/>
            </Dropdown>
         
         </span>
    },
  },

];
const ProductsList = ({productsList}) => {
  return (
    <Widget
      title={
        <h2 className="h4 gx-text-capitalize gx-mb-0">
          Products</h2>
      }
      extra={
        <p className="gx-text-primary gx-mb-0 gx-pointer gx-d-none gx-d-sm-block">
          <i className="icon icon-add-circle gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> Add New Product</p>
      }>
      <div className="gx-table-responsive">
        <Table className="gx-table-no-bordered" columns={columns} dataSource={productsList} pagination={true}
               size="small"/>
      </div>
      <p className="gx-text-primary gx-mb-0 gx-pointer gx-d-block gx-d-sm-none gx-mb-0 gx-mt-3">
        <i className="icon icon-add-circle gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> Add New Product</p>
    </Widget>
  );
};

export default ProductsList;
