import React, {Component} from "react";
import {Button, Checkbox, Drawer, message} from "antd";
import CustomScrollbars from "util/CustomScrollbars";

import userList from "./data/userList";
import UsersList from "components/lists/UsersList/UserList";
import AppModuleHeader from "components/AppModuleHeader/index";
import AddUser from "components/lists/UsersList/AddUser";
import IntlMessages from "util/IntlMessages";

let userId = 723812738;

const filterOptions = [
  {
    id: 1,
    name: 'All Users',
    icon: 'all-contacts'
  }, {
    id: 2,
    name: 'Active Users',
    icon: 'star'

  }, {

    id: 3,
    name: 'Inactive Users',
    icon: 'frequent'
  }
];

class Users extends Component {

  UserSideBar = (user) => {
    return <div className="gx-module-side">
      <div className="gx-module-side-header">
        <div className="gx-module-logo">
          <i className="icon icon-contacts gx-mr-4"/>
          <span><IntlMessages id="chat.users"/></span>
        </div>
      </div>

      <div className="gx-module-side-content">
        <CustomScrollbars className="gx-module-side-scroll">
          <div className="gx-module-add-task">
            <Button className="gx-btn-block ant-btn" type="primary" aria-label="add"
                    onClick={this.onAddUser}>
              <i className="icon icon-add gx-mr-2"/>
              <span>Add New User</span>
            </Button>
          </div>
          <div className="gx-module-side-nav">
            <ul className="gx-module-nav">
              {filterOptions.map(option => <li key={option.id} className="gx-nav-item">
                  <span
                    className={`gx-link ${option.id === this.state.selectedSectionId ? 'active' : ''}`} onClick={
                    this.onFilterOptionSelect.bind(this, option)
                  }>
                    <i className={`icon icon-${option.icon}`}/>
                    <span>{option.name}</span>
                  </span>
                </li>
              )}

            </ul>
          </div>
        </CustomScrollbars>
      </div>
    </div>

  };

  addFavourite = (data) => {
    this.setState({
      alertMessage: data.starred ? 'User removed as star' : 'User marked as active',
      showMessage: true,
      userList: this.state.userList.map((user) => user.id === data.id ? {
        ...user,
        starred: !data.starred
      } : user),
      allUser: this.state.allUser.map((user) => user.id === data.id ? {
        ...user,
        starred: !data.starred
      } : user)
    })
  };
  onUserSelect = (data) => {
    data.selected = !data.selected;
    let selectedUsers = 0;
    const userList = this.state.userList.map(user => {
        if (user.selected) {
          selectedUsers++;
        }
        if (user.id === data.id) {
          if (user.selected) {
            selectedUsers++;
          }
          return data;
        } else {
          return user;
        }
      }
    );
    this.setState({
      selectedUsers: selectedUsers,
      userList: userList
    });

  };


  onAddUser = () => {
    this.setState({AddUserState: true});
  };
  onUserClose = () => {
    this.setState({AddUserState: false});
  };
  onFilterOptionSelect = (option) => {
    switch (option.name) {
      case 'All users': {
        this.setState({
          selectedSectionId: option.id,
          filterOption: option.name,
          userList: this.state.allUser
        });
        break;
      }
      case 'Frequently usered': {
        this.setState({
          selectedSectionId: option.id,
          filterOption: option.name,
          userList: this.state.allUser.filter((user) => user.frequently)
        });
        break;
      }
      case 'Starred users': {
        this.setState({
          selectedSectionId: option.id,
          filterOption: option.name,
          userList: this.state.allUser.filter((user) => user.starred)
        });
        break;
      }
      default:
        break;
    }

  };
  onSaveUser = (data) => {
    let isNew = true;
    const userList = this.state.allUser.map((user) => {
      if (user.id === data.id) {
        isNew = false;
        return data
      } else {
        return user
      }
    });
    if (isNew) {
      userList.push(data);
    }
    this.setState({
      alertMessage: isNew ? 'User Added Successfully' : 'User Updated Successfully',
      showMessage: true,
      userList: userList,
      allUser: userList
    });
    // this.onFilterOptionSelect(this.state.filterOption);
  };
  onDeleteUser = (data) => {
    this.setState({
      alertMessage: 'User Deleted Successfully',
      showMessage: true,
      allUser: this.state.allUser.filter((user) => user.id !== data.id),
      userList: this.state.allUser.filter((user) => user.id !== data.id)
    })
  };
  onDeleteSelectedUser = () => {
    const users = this.state.allUser.filter((user) => !user.selected);
    this.setState({
      alertMessage: 'User Deleted Successfully',
      showMessage: true,
      allUser: users,
      userList: users,
      selectedUsers: 0
    })
  };
  filterUser = (userName) => {
    const {filterOption} = this.state;
    if (userName === '') {
      this.setState({userList: this.state.allUser});
    } else {
      const filterUser = this.state.allUser.filter((user) =>
        user.name.toLowerCase().indexOf(userName.toLowerCase()) > -1);
      if (filterOption === 'All users') {
        this.setState({userList: filterUser});
      } else if (filterOption === 'Frequently usered') {
        this.setState({userList: filterUser.filter((user) => user.frequently)});

      } else if (filterOption === 'Starred users') {
        this.setState({userList: filterUser.filter((user) => user.starred)});
      }
    }
  };
  handleRequestClose = () => {
    this.setState({
      showMessage: false,
    });
  };
  getAllUser = () => {
    let userList = this.state.allUser.map((user) => user ? {
      ...user,
      selected: true
    } : user);
    this.setState({
      selectedUsers: userList.length,
      allUser: userList,
      userList: userList
    });
  };
  getUnselectedAllUser = () => {
    let userList = this.state.allUser.map((user) => user ? {
      ...user,
      selected: false
    } : user);
    this.setState({
      selectedUsers: 0,
      allUser: userList,
      userList: userList
    });
  };

  constructor() {
    super();
    this.state = {
      noContentFoundMessage: 'No User found in selected folder',
      alertMessage: '',
      showMessage: false,
      selectedSectionId: 1,
      drawerState: false,
      user: {
        name: 'Robert Johnson',
        email: 'robert.johnson@example.com',
        avatar: "https://via.placeholder.com/150"
      },
      searchUser: '',
      filterOption: 'All users',
      allUser: userList,
      userList: userList,
      selectedUser: null,
      selectedUsers: 0,
      AddUserState: false,
    }
  }

  onAllUserSelect() {
    const selectAll = this.state.selectedUsers < this.state.userList.length;
    if (selectAll) {
      this.getAllUser();
    } else {
      this.getUnselectedAllUser();
    }
  }

  updateUser(evt) {
    this.setState({
      searchUser: evt.target.value,
    });
    this.filterUser(evt.target.value)
  }

  onToggleDrawer() {
    this.setState({
      drawerState: !this.state.drawerState
    });
  }

  render() {
    const {user, userList, AddUserState, drawerState, selectedUsers, alertMessage, showMessage, noContentFoundMessage} = this.state;
    return (
      <div className="gx-main-content">
        <div className="gx-app-module">

          <div className="gx-d-block gx-d-lg-none">
            <Drawer
              placement="left"
              closable={false}
              visible={drawerState}
              onClose={this.onToggleDrawer.bind(this)}>
              {this.UserSideBar()}
            </Drawer>
          </div>
          <div className="gx-module-sidenav gx-d-none gx-d-lg-flex">
            {this.UserSideBar(user)}
          </div>

          <div className="gx-module-box">
            <div className="gx-module-box-header">
              <span className="gx-drawer-btn gx-d-flex gx-d-lg-none">
                  <i className="icon icon-menu gx-icon-btn" aria-label="Menu"
                     onClick={this.onToggleDrawer.bind(this)}/>
              </span>

              <AppModuleHeader placeholder="Search User" notification={false} apps={false}
                               user={this.state.user}
                               onChange={this.updateUser.bind(this)}
                               value={this.state.searchUser}/>
            </div>
            <div className="gx-module-box-content">

              <div className="gx-module-box-topbar">
                <Checkbox color="primary" className="gx-icon-btn"
                          indeterminate={selectedUsers > 0 && selectedUsers < userList.length}
                          checked={selectedUsers > 0}
                          onChange={this.onAllUserSelect.bind(this)}
                          value="SelectMail"/>


                {selectedUsers > 0 &&
                <i className="gx-icon-btn icon icon-trash" onClick={this.onDeleteSelectedUser.bind(this)}/>}

              </div>
              <CustomScrollbars className="gx-module-content-scroll">
                {userList.length === 0 ?
                  <div className="gx-h-100 gx-d-flex gx-align-items-center gx-justify-content-center">
                    {noContentFoundMessage}
                  </div>
                  : <UsersList userList={userList}
                                 addFavourite={this.addFavourite.bind(this)}
                                 onUserSelect={this.onUserSelect.bind(this)}
                                 onDeleteUser={this.onDeleteUser.bind(this)}
                                 onSaveUser={this.onSaveUser.bind(this)}/>
                }


              </CustomScrollbars>

            </div>
          </div>
        </div>

        <AddUser open={AddUserState} user={{
          'id': userId++,
          'name': '',
          'thumb': '',
          'email': '',
          'phone': '',
          'designation': '',
          'selected': false,
          'starred': false,
          'frequently': false,
        }} onSaveUser={this.onSaveUser}
                    onUserClose={this.onUserClose} onDeleteUser={this.onDeleteUser}/>

        {showMessage && message.info(<span id="message-id">{alertMessage}</span>, 3, this.handleRequestClose)}
      </div>
    )
  }
}

export default Users;
