import React from "react";
import {Avatar} from "antd";

const UserCell = ({code, selectedSectionId, onSelectUser}) => {
  return (
    <div className={`gx-chat-user-item ${selectedSectionId === code.id ? 'active' : ''}`} onClick={() => {
      onSelectUser(code);
    }}>
      <div className="gx-chat-user-row">
       
        <div className="gx-chat-info">
          <span className="gx-name h4">{code.name}</span>
          <div className="gx-chat-info-des gx-text-truncate">{code.lastMessage.substring(0, 25) + "..."}</div>
          <div className="gx-last-message-time">{code.lastMessageTime}</div>
        </div>

        {code.unreadMessage > 0 ? <div className="gx-chat-date">
          <div className="gx-bg-primary gx-rounded-circle gx-badge gx-text-white">{code.unreadMessage}</div>
        </div> : null}
      </div>
    </div>
  )
};

export default UserCell;
