import React from "react";
import {Table,Dropdown,Menu} from "antd";
import Widget from "components/Widget/index";

const options = [
  'Edit',
  'Delete',
];

const menus = () => (<Menu onClick={(e) => {
  if (e.key === 'Edit') {    
  } else {
   
  }
}
}>
  {options.map(option =>
    <Menu.Item key={option}>
      {option}
    </Menu.Item>,
  )}
</Menu>);

const columns = [
  {
    title: 'Policyholder Name',
    dataIndex: 'image',
    sorter: (a, b) => a.name - b.name,
    render: (text, record) => {
      return <div className="gx-flex-row gx-align-items-center"> 
      <img className="gx-rounded-circle gx-size-30 gx-mr-2" src={text} alt=""/>      
        <p className="gx-mb-0">{record.name}</p>
      </div>
    },
  },
  {
    title: 'Email',
    dataIndex: 'transfer',
    sorter: (a, b) => a.email - b.email,
    render: (text, record) => {
      return <span className="gx-text-grey">{record.email}</span>
    },

  },
  {
    title: 'Product',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.product}</span>
    },

  },
  {
    title: 'Medical Scheme',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.medical_scheme}</span>
    },

  },
  {
    title: 'Date Entered',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.date_entered}</span>
    },

  },
  {
    title: 'Status',
    dataIndex: 'transfer',
    render: (text, record) => {
      return <span className="gx-text-grey">{record.p_status}</span>
    },

  },
  {
    title: 'Action',
    dataIndex: 'status',
    render: (text) => {
      return <span className="gx-text-primary gx-pointer gx-d-inline-flex">
        <i className="icon icon-forward gx-fs-sm gx-mr-2"/>{text}
        <Dropdown overlay={menus} placement="bottomRight" trigger={['click']}>
              <i className="icon icon-ellipse-v"/>
            </Dropdown>
         
         </span>
    },
  },

];
const PolicyholdersList = ({policyholdersList}) => {
  return (
    <Widget
      title={
        <h2 className="h4 gx-text-capitalize gx-mb-0">
          Policyholders</h2>
      }>
      <div className="gx-table-responsive">
        <Table className="gx-table-no-bordered" columns={columns} dataSource={policyholdersList} pagination={true}
               size="small"/>
      </div>
      <p className="gx-text-primary gx-mb-0 gx-pointer gx-d-block gx-d-sm-none gx-mb-0 gx-mt-3">
        <i className="icon icon-add-circle gx-fs-lg gx-d-inline-flex gx-vertical-align-middle"/> Add New Policyholder</p>
    </Widget>
  );
};

export default PolicyholdersList;
