import React from "react";
import {Avatar, Dropdown, Menu} from "antd";
import CustomScrollbars from 'util/CustomScrollbars'

import labels from "routes/lists/Doctors/data/labels";

const options = [
  'Reply',
  'Forward',
  'Print',
];

class DoctorDetail extends React.Component {

  state = {
    showDetail: false
  };

  optionMenu = () => {
    return (
      <Menu id="long-menu">
        {options.map(option =>
          <Menu.Item key={option}>
            {option}
          </Menu.Item>,
        )}
      </Menu>)
  };

  render() {
    const {doctor, onStartSelect, onImportantSelect} = this.props;

    return (
      <div className="gx-module-detail gx-mail-detail">
        <CustomScrollbars className="gx-module-content-scroll">
          <div className="gx-mail-detail-inner">
            <div className="gx-mail-header">

              <div className="gx-mail-header-content gx-col gx-pl-0">
                <div className="gx-subject">
                 Doctor details here
                </div>



              </div>


            </div>
            <hr/>



            <p>
              {doctor.message}
            </p>

            {doctor.hasAttachments &&
            <div className="gx-attachment-block">
              <h3>Attachments ({doctor.attachments.length})</h3>
              <div className="gx-attachment-row">
                {doctor.attachments.map((attachment, index) =>
                  <div className="gx-attachment-col" key={index}>
                    <img src={attachment.preview} alt={attachment.fileName}/>
                    <div className="size">{attachment.size}</div>
                  </div>
                )}
              </div>
            </div>
            }
          </div>
        </CustomScrollbars>
      </div>
    );
  }
}

export default DoctorDetail;
